import React, { useState } from "react";
import JSONDATA from "./Services.json";

export default function SearchServices() {
  const [data, setData] = useState("");
  return (
    <>
      <section>
        <div className="container-fluid bg-light hght">
          <div className="container">
            <h2
              style={{
                color: "#6e736f",
                marginTop: "2px",
                marginBottom: "32px",
                fontSize:"35px"
              }}
            >
              <b>All Services</b>
            </h2>
            <form className="d-flex  " role="search">
              <input
                className="form-control me-3 mb-4  service-input input-width"
                type="text"
                placeholder="Search"
                aria-label="Search"
                onChange={(event) => {
                  setData(event.target.value);
                }}
              />
              <button className="btn btn-warning p-2 mb-4 btnn " type="submit">
                SEARCH
              </button>
            </form>
            <div className="container-fluid bg-white">
            <div className="row ">
              <div className="col-md-4">
              {/* <b>CONSULT AN EXPERT</b> */}
                {JSONDATA.filter((value) => {
                  
                  if (data == "") {
                    return value;
                  } else if (
                    value.consult?.toLowerCase().includes(data?.toLowerCase())
                  ) {
                    return value;
                  }
                }).map((val) => {
                  return (
                    <div >
                      <ul>
                
                        <li>
                          <a href="#" className="search_ser">
                            {val.consult}
                          </a>
                        </li>
                      </ul>
                    </div>
                  );
                })}
              </div>

              <div className="col-md-4">
                {/* <b>TAX & COMPLIANCE</b> */}
                {JSONDATA.filter((value) => {
                  if (data == "") {
                    return value;
                  } else if (
                    value.tax?.toLowerCase().includes(data?.toLowerCase())
                  ) {
                    return value;
                  }
                }).map((val) => {
                  return (
                    <div>
                      <ul>
                        <li>
                          <a href="#" className="search_ser">
                            {val.tax}
                          </a>
                        </li>
                      </ul>
                    </div>
                  );
                })}
              </div>

              <div className="col-md-4">
                {/* <b>DOCUMENTATION</b> */}
                {JSONDATA.filter((value) => {
                  if (data == "") {
                    return value;
                  } else if (
                    value.documentation
                      ?.toLowerCase()
                      .includes(data?.toLowerCase())
                  ) {
                    return value;
                  }
                }).map((val) => {
                  return (
                    <div>
                      <ul>
                        <li>
                          <a href="#" className="search_ser">
                            {val.documentation}
                          </a>
                        </li>
                      </ul>
                    </div>
                  );
                })}
              </div>
            </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
